mapper.insert:
   	INSERT INTO TB_USER (
    	USER_SEQ
		,USER_ID
		,USER_NAME
		,PHONE_NUMBER
		,USER_PASSWORD
		,USER_EMAIL
		,USER_KEY
		,CREA_DATA
		,USER_IMAGE_PATH
		,USER_ROLL
		,USER_ACTIVATED
	 )VALUES (
	   	${userSeq}
		,#{userId}	
		,#{userName} 
		,#{phoneNumber}
		,#{userPassword}
		,#{userEmail}
		,(SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') || LPAD(${userSeq}, 10, 0) FROM DUAL)
		,SYSDATE
		,#{userImagePath}
		,#{userRoll}
		,${userActivated}
   	)

mapper.select:
			SELECT  * FROM TB_USER WHERE 
			USER_ID=#{userId} 
   			AND USER_PASSWORD=#{userPassword}

   				AND USER_NAME=#{userName}


   				AND PHONE_NUMBER=#{phoneNumber}


   				AND USER_EMAIL=#{userEmail}

			SELECT  * FROM TB_USER WHERE 1=1

   				USER_ID=#{userId} 


   				AND USER_KEY=#{userKey}



   				AND USER_NAME=#{userName}


   				AND PHONE_NUMBER=#{phoneNumber}


   				AND USER_EMAIL=#{userEmail}
